svg.script:
var SVGDoc = null
	
function getObj(mouseEvent) {
	SVGDoc = document
}
	
// change the filter effect of an element
function elemFilter(mouseEvent, elemName, value) {
	// get the proper SVG object
	getObj(mouseEvent)

	// get the element we want to change
	var thisElem = SVGDoc.getElementById(elemName).style

	// perform the filter effect change
	thisElem.setProperty('filter', value, '')
}
	
// change the display of an element
function elemDisplay(mouseEvent, elemName, value) {
	// get the proper SVG object
	getObj(mouseEvent)

	// get the element we want to change
	var thisElem = SVGDoc.getElementById(elemName).style

	// perform the display change
	thisElem.setProperty('display', value, '')
}
