svg.script:
  var canvas = document.getElementById("canvas");
  var point = document.getElementById("point");
  var running = false;
  var y = 250;

  function start(evt) {
    if (running) return;
    running = true;
    evt.target.setAttribute('stroke', 'blue');
    canvas.setAttribute('fill', 'none');
    canvas.setAttribute("points", "" + document.getElementById(evt.target.firstChild.data).getAttribute("d").split(" ")[1] + " ");
    document.getElementById("line").setAttributeNS("http://www.w3.org/1999/xlink", 'href', '#' + evt.target.firstChild.data);
    canvas.setAttribute('stroke', document.getElementById(evt.target.firstChild.data).getAttribute('stroke'));
    document.getElementById("anim").beginElement();
    window.setTimeout("draws(333)", 50); // how many draws MAX to do...
    window.setTimeout(function(){end(evt)}, 12000);
    document.getElementById("pen").setAttribute('display', 'block');
  }

  function draws(cnt) {
    var c = point.getScreenCTM();
    canvas.setAttribute("points", "" + canvas.getAttribute("points") + " " + c.e + "," + c.f );
    if (cnt) window.setTimeout("draws(" + (--cnt) +")", 40);
  }

  function end(evt) {
    evt.target.setAttribute('stroke', 'none');
    document.getElementById("grd").setAttribute('stop-color', document.getElementById(evt.target.firstChild.data).getAttribute('fill'));
    canvas.setAttribute('fill', "url(#flx)");
    document.getElementById("pen").setAttribute('display', 'none');
    document.getElementById("filler").beginElement();
    running = false;
  }
  
  // display clickable animals names
  var anims = document.getElementById('animals').getElementsByTagName('path');
  for (var i in anims) {
    if (!anims[i].getAttribute) continue;
    y = y + 22;
    var b = document.getElementById("txt").firstChild.cloneNode(true);
    b.setAttribute('y', y);
    document.getElementById("txt").appendChild(b);
    b.firstChild.data = anims[i].getAttribute('id');
    b.addEventListener('click', function(evt){start(evt)}, false);
  }
