svg.script:
var txt = document.getElementById("txt").firstChild;
var c = document.getElementById("myc");
var l = document.getElementById("myl");

function check() {
  var cc = new Circle(c);
  var ll = new Line(l);
  var inter = Intersection.intersectShapes(cc, ll);
  txt.data = inter.status;
}

window.setInterval(check, 500);

function mouseTracker(evt) {
  l.setAttribute("x2", evt.clientX);
  l.setAttribute("y2", evt.clientY);
}
